// Exit if accessed directly
if ( ! defined( 'ABSPATH' ) ) exit;

/**
 * Creates the admin submenu pages under the Quick AdSense Reloaded menu and assigns their
 * links to global variables
 *
 * @since 1.0
 * @global $quads_settings_page
 * @global $quads_add_ons_page
 * @return void
 */
function quads_add_options_link() {
    global $quads_options, $quads_parent_page, $quads_add_ons_page, $quads_add_ons_page2,$quads_permissions, $quads_settings_page, $quads_mode;

    $quads_mode = get_option('quads-mode');

    $label = quads_is_extra() ? 'WP QUADS PRO' : 'WP QUADS';

    $create_settings = isset($quads_options['create_settings']) ? true : false;
    if ($create_settings && $quads_mode != 'new') {
        $quads_settings_page = add_submenu_page('options-general.php', __('WP QUADS Settings', 'quick-adsense-reloaded'), __('WPQUADS', 'quick-adsense-reloaded'),$quads_permissions, 'quads-settings', 'quads_options_page');
    } else {
        $wpquads_logo = "
";

        if($quads_mode == 'new'){

            $quads_parent_page = add_menu_page('Quick AdSense Reloaded Settings', $label, $quads_permissions, 'quads-settings', 'quads_options_page_new', 'data:image/svg+xml;base64,' . $wpquads_logo);

            $quads_settings_page = add_submenu_page('quads-settings', __('Ads', 'quick-adsense-reloaded'), 'Ads', $quads_permissions, 'quads-settings', 'quads_options_page_new');

	        if( defined('QUADS_PRO_VERSION') ){
                $license_alert = $days = '';
                $license_info = get_option( 'quads_wp_quads_pro_license_active' );
                if ( isset( $license_info ) ) {
                    $license_exp = isset( $license_info->expires ) ? date('Y-m-d', strtotime( $license_info->expires) ) : '' ;
                    $license_info_lifetime = isset( $license_info->expires ) ? $license_info->expires : '' ;
                    $today = date('Y-m-d');
                    $exp_date = $license_exp;
                    $date1 = date_create($today);
                    $date2 = date_create($exp_date);
                    $diff = date_diff($date1,$date2);
                    $days = $diff->format("%a");
                    if( $license_info_lifetime == 'lifetime' ){
                    $days = 'Lifetime';
                    if ($days == 'Lifetime') {
                    $expire_msg = " Your License is Valid for Lifetime ";
                    }
                    }
                    else if($today > $exp_date){
                    $days = -$days;
                    }
                $license_alert = isset($days) && $days!==0 && $days<=30 && $days!=='Lifetime' ? "<span class='wpquads_pro_icon dashicons dashicons-warning wpquads_pro_alert' style='color: #ffb229;left: 3px;position: relative;'></span>": "" ;
                }
                if(quads_has_setting_access()){
                    $quads_settings_page = add_submenu_page('quads-settings', __('Settings', 'quick-adsense-reloaded'), 'Settings'.$license_alert.'', $quads_permissions, 'quads-settings&path=settings', 'quads_options_page_new');
                }            
            }
            else{
                if(quads_has_setting_access()){
                    $quads_settings_page = add_submenu_page('quads-settings', __('Settings', 'quick-adsense-reloaded'), 'Settings', $quads_permissions, 'quads-settings&path=settings', 'quads_options_page_new');
                }
            }
            if( isset($quads_options['reports_settings']) && $quads_options['reports_settings'] == 1 )
            $quads_settings_page = add_submenu_page('quads-settings', __('Reports', 'quick-adsense-reloaded'), 'Reports', $quads_permissions, 'quads-settings&path=reports', 'quads_options_page_new');

            add_submenu_page('quads-settings', __('Return to Classic view', 'quick-adsense-reloaded'), 'Return to Classic view', $quads_permissions, 'quads_switch_to_old', 'quads_version_switch');

        }else{
            $quads_parent_page = add_menu_page('Quick AdSense Reloaded Settings', $label, 'manage_options', 'quads-settings', 'quads_options_page', 'data:image/svg+xml;base64,' . $wpquads_logo);

            $quads_settings_page = add_submenu_page('quads-settings', __('Ad Settings', 'quick-adsense-reloaded'), 'Ad Settings', 'manage_options', 'quads-settings', 'quads_options_page');

            add_submenu_page('quads-settings', __('Switch to New Interface', 'quick-adsense-reloaded'), 'Switch to New Interface', 'manage_options', 'quads_switch_to_new', 'quads_version_switch');

        }

        if (quads_is_extra() || quads_is_advanced()) {

        }else{
            $quads_add_ons_page = add_submenu_page('quads-settings', __('Get Add-On', 'quick-adsense-reloaded'), 'Upgrade to PRO', 'manage_options', 'quads-addons', 'quads_add_ons_page');
        }
    }
}

add_action( 'admin_menu', 'quads_add_options_link', 10 );

/**
 *  Determines whether the current admin page is an QUADS add-on page.
 *
 *  Only works after the `wp_loaded` hook, & most effective
 *  starting on `admin_menu` hook.
 *
 *  @since 1.4.9
 *  @return bool True if QUADS admin page.
 */
function quads_is_addon_page() {
        $currentpage = isset($_GET['page']) ? $_GET['page'] : '';
	if ( ! is_admin() || ! did_action( 'wp_loaded' ) ) {
		return false;
	}

	if ( 'quads-addons' == $currentpage ) {
		return true;
	}
}

function quads_has_setting_access(){
    global $quads_options;
    $user = wp_get_current_user();
    $roles = ( array ) $user->roles;
    $role_access = isset($quads_options['RoleBasedAccess'])?$quads_options['RoleBasedAccess']:[];
    foreach ($roles as $role) {
        if ($role == 'administrator' || $role == 'super_admin') {
            return true; // User has access
        }
        foreach ($role_access as $roleAccess) {
            if ($roleAccess['value'] === $role && $roleAccess['setting_access'] === true) {
                return true; // User has access
            }
        }
    }
    return false;
}